import React, { useEffect, useReducer } from 'react';
import './App.css';
import fetchGraphql from './fetchGraphql'

function reducer(state, action) {

  let newState = {...state}
    console.log(action)
  switch (action.type) {
    case 'search':
      newState.search = action.input
      break;
    case 'clear_search':
          newState.search = "";
          newState.show = newState.sermons;
      break;
    case 'submit':
      newState.show = filterSermons(newState.sermons, newState.search)
      break;

      case 'sermons_success':

          newState.sermons = action.payload.search.recordings
          newState.show = action.payload.search.recordings
          newState.search = ''
          newState.pending = false
          break;

      case 'sermons_error':
          newState.error = action.payload
          newState.pending = false
          break;

      case 'sermons_pending':
          newState.pending = true
          break;

    default:
      throw new Error();
  }

  return newState;
}

function filterSermons(list, search) {
    return list.filter(function(sermon){
            return sermon.Key.toLowerCase().includes(search.toLowerCase()) || sermon.Title.toString().toLowerCase().includes(search.toLowerCase())
        })
}

function App() {

  const initialState = {sermons:[], show:[], search:"", pending:false};
  const [state, dispatch] = useReducer(reducer, initialState);

  const svgStyle = {
    lineHeight:"normal",
    fill:"#f14e32",
    textIndent:0,
    textAlign:"start",
    textDecorationLine:"none",
    textDecorationStyle:"solid",
    textDecorationColor:"#000",
    textTransform:"none",
    blockProgression:"tb",
    isolation:"auto",
    mixBlendMode:"normal"
  }

    // Note: the empty deps array [] means
    // this useEffect will run once
    // similar to componentDidMount()

    useEffect(() => {
        const query = {
            operationName: "Query", variables: {}, query: `query Query {
              search(q: "") {
                q
                recordings {
                  Key
                  Title
                }
                verses {
                  id
                  reference
                  kjv {
                    id
                    b
                    c
                    v
                    t
                  }
                }
                people {
                  id
                }
              }
            }
            `}

        const actions = {
            pending: (payload) => {
                dispatch({ type: "sermons_pending", payload: payload })
            },
            success: (payload) => {
                dispatch({ type: "sermons_success", payload: payload })
            },
            error: (payload) => {
                dispatch({ type: "sermons_error", payload: payload })
            },
        }

        fetchGraphql(query, actions)
    }, [])

    const query = new URLSearchParams(window.location.search).get('play');
    console.log(query)
  return (
      <div className="App">

          {query && <audio style={{ width: "100%" }} controls={true} autoPlay={true} name="media">
              <source src={query} type="audio/mp3" />
          </audio>}

          <h1 id="sermons">Sermons</h1>

          {state.error && state.error.map(function (err) {
              return <li>{err.message}</li>
          })}

          <h3>Search ({state.show.length}):</h3>

          
          <input style={{ width: "80%", padding: "15px" }}
              value={state.search}
              type="text"
              name="sermonssearch"
              placeholder="enter text"
              onChange={(e)=> {dispatch({type: 'search' , input: e.target.value})} }
          />

          <img
              onClick={(e) => { dispatch({ type: 'submit' }) }} 
              style={{ width: "50px" }}
              src="data:image/png;base64," />
         
      <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 50 50" height="35px" 
      style={{width:"35px", margin:0, padding:0}} 
      alt="Image result for clear svg icon" onClick= {(e)=> {dispatch({type: 'clear_search'})} }>
          <path style={svgStyle} 
          d="M 12.541016 10 L -0.31640625 25 L 12.541016 40 L 13 40 L 46 40 C 48.197334 40 50 38.197334 50 36 L 50 14 C 50 11.802666 48.197334 10 46 10 L 12.541016 10 z M 13.458984 12 L 46 12 C 47.116666 12 48 12.883334 48 14 L 48 36 C 48 37.116666 47.116666 38 46 38 L 13.460938 38 L 2.3164062 25 L 13.458984 12 z M 22.707031 18.292969 L 21.292969 19.707031 L 26.585938 25 L 21.292969 30.292969 L 22.707031 31.707031 L 28 26.414062 L 33.292969 31.707031 L 34.707031 30.292969 L 29.414062 25 L 34.707031 19.707031 L 33.292969 18.292969 L 28 23.585938 L 22.707031 18.292969 z" 
          fontWeight="400" fontFamily="sans-serif" white-space="normal" overflow="visible"/>
      </svg>

      

          <ul style={{ textAlign:"left" }}>
          {state.show.map((sermon) => {

              if (sermon.Key.includes('.json') !== false) {
     
              } else if (sermon.Key.includes('.mp3') === false && sermon.Key.includes('.MP3') === false && sermon.Key.includes('.wav') === false) {
                  return <li><h2>{sermon.Title}</h2></li>
              } else {
                  return <li><a href={"/sermons?play=https://media.deliverance.me/" + sermon.Key + "#sermons"}>{sermon.Title}</a></li>
              }
          
        })}
      </ul>

    </div>
  );

}

export default App;
